INSERT INTO public.courses (course_id, title, type, description, rating, level, thumbnail_url, headline, content_info, amount_price, currency, course_slug, access_count, total_students, created_at, updated_at, teacher_id) VALUES ('90f5c3b5-6b82-4b6d-85b5-9c1d414e0010', 'Khóa Học Tiếng Anh Phát Âm Chuẩn', 'paid', e'Hướng dẫn cách phát âm tiếng Anh chuẩn và cải thiện khả năng nghe hiểu. Điều này sẽ đưa bạn vào một hành trình phát âm tiếng Anh chuẩn và cải thiện khả năng nghe hiểu một cách tự tin và hiệu quả. Khóa học này không chỉ tập trung vào việc rèn luyện phát âm đúng và rõ ràng, mà còn cung cấp các chiến lược và kỹ thuật để bạn có thể hiểu rõ và phản ứng linh hoạt với nhiều loại giọng điệu và tốc độ khác nhau.

